'use strict';

exports.alert = function (title, message) {
    var promise = new Promise(function (resolve) {
        alert(message);
        resolve();
    });

    return promise;
};

exports.prompt = function (title, message, value, options) {
    var o = options,
        promise = new Promise(function (resolve, reject) {
        var noCancel = o && !o.cancel,
            result = prompt(message, value);

        if (result !== null) resolve(result);else if (!noCancel) reject();
    });

    return promise;
};

exports.confirm = function (title, message, options) {
    var o = options,
        noCancel = o && !o.cancel,
        promise = new Promise(function (resolve, reject) {
        var is = confirm(message);

        if (is || noCancel) resolve();else reject();
    });

    return promise;
};